var link=document.querySelector(".map-contact-btn"),popup=document.querySelector(".modal-content"),close=popup.querySelector(".modal-content-close"),form=popup.querySelector("form"),login=popup.querySelector("[name=name]"),password=popup.querySelector("[name=email]"),textarea=popup.querySelector("[name=letter]"),overlay=document.querySelector(".modal-overlay");link.addEventListener("click",function(a){a.preventDefault(),popup.classList.add("modal-content-show"),overlay.classList.add("overlay-show");var b=localStorage.getItem("name");if(b){login.value=b;var c=localStorage.getItem("email");password.value=c,textarea.focus()}else login.focus()}),close.addEventListener("click",function(a){a.preventDefault(),popup.classList.remove("modal-content-show"),overlay.classList.remove("overlay-show"),popup.classList.remove("modal-error")}),form.addEventListener("submit",function(a){login.value&&password.value&&textarea.value?(localStorage.setItem("name",login.value),localStorage.setItem("email",password.value)):(a.preventDefault(),popup.classList.add("modal-error"))}),window.addEventListener("keydown",function(a){27===a.keyCode&&popup.classList.contains("modal-content-show")&&(popup.classList.remove("modal-content-show"),overlay.classList.remove("overlay-show"),popup.classList.remove("modal-error"))});
